-- Create letters table
CREATE TABLE letters (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    customer_id INTEGER,
    no_surat TEXT,
    jenis_surat TEXT,
    tanggal_surat TEXT,
    file_path TEXT,
    status TEXT DEFAULT 'draft',
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (customer_id) REFERENCES customers(id)
);

